!function(){        require.async(['detail:widget/shop_map/shopMap.js', 'detail:widget/shop_map/main.js'], function (ShopMap,main) {
            var myGeo = null;
            var lat = '39.969497';
            var lon = '116.395754';
            if (lat.length < 1 || lon.length < 1) {
                myGeo = new BMap.Geocoder();
                myGeo.getPoint('北京市朝阳区黄寺大街15号-106', function (point) {
                    point = [
                        {
                            lat: point.lat,
                            lon: point.lng
                        }
                    ];
                    initShopMap(point);
                });
            }
            else {
                initShopMap([{lat: lat, lon: lon}]);
            }

            function initShopMap(point) {
                var sm = new ShopMap({
                    main: 'j-map-container',
                    point: point
                });
                sm.addPin(point);
                main.init({
                    shop:{
                        name:'宏状元粥店(黄寺大街店)',
                        address:'北京市朝阳区黄寺大街15号-106',
                        phone:'01082095662'
                    },
                    point: point
                });
            }
        });
    }();
