                                }
                                // 不管有没有baiduid，都发送pv
                                sendPv();
                            } else {
                                if (retryCount--) {
                                    getBaiduid();
                                } else {
                                    // 最后一次也获取失败
                                    sendPv();
                                }
                            }
                        }
                    });
